function paddle_svg_allowedHtml()
{
    $allowed_html = array(
        'svg'    => array(
            'title'           => true,
            'class'           => true,
            'aria-hidden'     => true,
            'aria-labelledby' => true,
            'role'            => true,
            'xmlns'           => true,
            'width'           => true,
            'height'          => true,
            'viewbox'         => true, // <= Must be lower case!
        ),
        'g'      => array('fill' => true),
        'title'  => array('title' => true, 'id' => true),
        'circle' => array('fill' => true, 'cx' => true, 'cy' => true, 'r' => true),
        'path'   => array(
            'd'    => true,
            'fill' => true,
            'cx' => true,
            'cy' => true,
            'r' => true,
        ),
    );
    return $allowed_html;
}

if (!function_exists('paddle_optimize_image_height_width_using_url')) {

    /**
     * paddle_optimize_image_height_width_using_url
     *
     * @param  mixed $image_url
     * @param  mixed $size
     * @param  mixed $classes
     * @param  mixed $alt
     * @param  mixed $style
     * @return void
     */
    function paddle_optimize_image_height_width_using_url($image_url, $size = 'full', $classes = '', $alt = '', $style = '')
    {
        if ('' !== $image_url) {
            $image_id   = attachment_url_to_postid($image_url);
            $image = wp_get_attachment_image_src($image_id, $size);

            if ($image) {
                $full_image_url = $image[0];
                $width = $image[1];
