if (!function_exists('paddle_comma_string_to_obj')) {

    /**
     * paddle_comma_string_to_obj
     *
     * @param  mixed $string - comma separated string
     * @param  mixed $name
     * @param  mixed $value
     * @return object
     */
    function paddle_comma_string_to_obj($string, $name, $value)
    {
        $array = array();
        // Create array from the commas separated text data.
        $string_array = explode(',', $string);

        if (!empty($string_array[0])) {

            // create array pair value
            $result = array();
            for ($i = 0; $i < count($string_array); $i += 2) {
                $array[$name] = $string_array[$i];
                $array[$value] = $string_array[$i + 1];
                array_push($result, $string_array[$i + 1]);
                $arrays[] = $array;
            }
            return $arrays;
        }
    }
}

